import React, { useState } from 'react';
import { 
  Code, 
  Database, 
  Cloud, 
  Brain, 
  Calculator, 
  Globe, 
  Server, 
  ArrowRight,
  Clock,
  Users,
  Star,
  Play,
  MessageCircle,
  Calendar
} from 'lucide-react';
import ContactForm from '../components/ContactForm';

const Courses = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [isDemo, setIsDemo] = useState(false);

  const courses = [
    {
      id: 1,
      title: 'Web Development',
      description: 'Master HTML, CSS, JavaScript, and modern web development frameworks. Build responsive and interactive websites from scratch.',
      duration: '12 weeks',
      students: '2,500+',
      rating: 4.8,
      price: '$299',
      category: 'development',
      icon: Code,
      color: 'blue',
      features: ['HTML5 & CSS3', 'JavaScript ES6+', 'React.js', 'Responsive Design', 'Git & GitHub', 'Deployment'],
      image: 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?w=400&h=250&fit=crop'
    },
    {
      id: 2,
      title: 'MERN Stack',
      description: 'Learn the complete MERN stack (MongoDB, Express.js, React.js, Node.js) to build full-stack web applications.',
      duration: '16 weeks',
