      category: 'devops',
      icon: Cloud,
      color: 'orange',
      features: ['AWS Services', 'Docker & Kubernetes', 'CI/CD Pipelines', 'Terraform', 'Monitoring', 'Security'],
      image: 'https://images.unsplash.com/photo-1451187580459-43490279c0fa?w=400&h=250&fit=crop'
    },
    {
