      image: 'https://images.unsplash.com/photo-1526379095098-d400fd0bf935?w=400&h=250&fit=crop'
    },
    {
      id: 5,
      title: 'AWS Linux',
      description: 'Master Linux administration on AWS. Learn server management, security, and cloud infrastructure.',
      duration: '8 weeks',
      students: '800+',
      rating: 4.6,
      price: '$199',
      category: 'devops',
      icon: Server,
      color: 'red',
      features: ['Linux Fundamentals', 'AWS EC2', 'Shell Scripting', 'Security', 'Monitoring', 'Backup'],
      image: 'https://images.unsplash.com/photo-1618401471353-b98afee0b2eb?w=400&h=250&fit=crop'
    },
    {
      id: 6,
      title: 'Digital Marketing & SEO',
      description: 'Learn digital marketing strategies, SEO optimization, social media marketing, and analytics.',
      duration: '12 weeks',
      students: '1,500+',
      rating: 4.7,
      price: '$299',
      category: 'marketing',
      icon: Globe,
      color: 'teal',
      features: ['SEO Optimization', 'Social Media Marketing', 'Google Ads', 'Analytics', 'Content Marketing', 'Email Marketing'],
      image: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=400&h=250&fit=crop'
    },
    {
      id: 7,
      title: 'AI/ML',
      description: 'Master artificial intelligence and machine learning. Learn algorithms, deep learning, and real-world applications.',
      duration: '20 weeks',
      students: '900+',
      rating: 4.9,
      price: '$599',
      category: 'ai',
      icon: Brain,
      color: 'purple',
      features: ['Machine Learning', 'Deep Learning', 'Neural Networks', 'TensorFlow/PyTorch', 'Computer Vision', 'NLP'],
      image: 'https://images.unsplash.com/photo-1677442136019-21780ecad995?w=400&h=250&fit=crop'
    },
    {
      id: 8,
      title: 'Mathematics',
      description: 'Master essential mathematics for computer science, data science, and engineering applications.',
      duration: '10 weeks',
      students: '1,200+',
      rating: 4.6,
      price: '$199',
      category: 'mathematics',
      icon: Calculator,
      color: 'indigo',
      features: ['Linear Algebra', 'Calculus', 'Statistics', 'Probability', 'Discrete Math', 'Optimization'],
      image: 'https://images.unsplash.com/photo-1635070041078-e363dbe005cb?w=400&h=250&fit=crop'
    }
  ];

  const categories = [
    { id: 'all', name: 'All Courses' },
    { id: 'development', name: 'Development' },
    { id: 'devops', name: 'DevOps' },
    { id: 'programming', name: 'Programming' },
    { id: 'marketing', name: 'Marketing' },
    { id: 'ai', name: 'AI/ML' },
    { id: 'mathematics', name: 'Mathematics' }
  ];

  const filteredCourses = selectedCategory === 'all' 
    ? courses 
    : courses.filter(course => course.category === selectedCategory);

  const handleWhatsApp = (courseTitle) => {
    const phoneNumber = '8142200317'; // Replace with your WhatsApp number
    const message = `Hi! I'm interested in the ${courseTitle} course. Can you provide more details about the curriculum, duration, and pricing?`;
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const openDemoForm = (course) => {
    setSelectedCourse(course.title);
    setIsDemo(true);
    setIsFormOpen(true);
  };

  const openPurchaseForm = (course) => {
    setSelectedCourse(course.title);
    setIsDemo(false);
    setIsFormOpen(true);
  };

  const closeForm = () => {
    setIsFormOpen(false);
    setSelectedCourse(null);
    setIsDemo(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 pt-16">
      {/* Hero Section */}
      <section className="bg-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center" data-aos="fade-up">
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6 font-heading">
              Explore Our Courses
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto mb-8">
              Choose from our comprehensive selection of courses designed by industry experts. 
              Start your journey towards a successful career in technology.
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              {categories.map((category) => (
                <button
                  key={category.id}
                  onClick={() => setSelectedCategory(category.id)}
                  className={`px-6 py-2 rounded-full font-medium transition-all duration-200 ${
                    selectedCategory === category.id
                      ? 'bg-primary-600 text-white shadow-lg'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Courses Grid */}
      <section className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredCourses.map((course, index) => (
              <div key={course.id} data-aos="fade-up" data-aos-delay={index * 100} className="card overflow-hidden group">
                {/* Course Image */}
                <div className="relative h-48 overflow-hidden">
                  <img
                    src={course.image}
                    alt={course.title}
                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-black/20 group-hover:bg-black/40 transition-colors duration-300"></div>
                  <div className="absolute top-4 right-4">
                    <div className={`w-12 h-12 bg-${course.color}-100 rounded-lg flex items-center justify-center`}>
                      <course.icon className={`h-6 w-6 text-${course.color}-600`} />
                    </div>
                  </div>
                </div>

                {/* Course Content */}
                <div className="p-6">
                  <div className="flex items-center justify-between mb-3">
                    <h3 className="text-xl font-bold text-gray-900 group-hover:text-primary-600 transition-colors duration-200">
                      {course.title}
                    </h3>
                    <span className="text-2xl font-bold text-primary-600">{course.price}</span>
                  </div>

                  <p className="text-gray-600 mb-4 line-clamp-3">
                    {course.description}
                  </p>

                  {/* Course Stats */}
                  <div className="flex items-center justify-between mb-4 text-sm text-gray-500">
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center">
                        <Clock className="h-4 w-4 mr-1" />
                        {course.duration}
                      </div>
                      <div className="flex items-center">
                        <Users className="h-4 w-4 mr-1" />
                        {course.students}
                      </div>
                    </div>
                    <div className="flex items-center">
                      <Star className="h-4 w-4 text-yellow-400 fill-current mr-1" />
                      {course.rating}
                    </div>
                  </div>

                  {/* Course Features */}
                  <div className="mb-6">
                    <h4 className="font-semibold text-gray-900 mb-2">What you'll learn:</h4>
                    <div className="grid grid-cols-2 gap-1">
                      {course.features.slice(0, 4).map((feature, idx) => (
                        <div key={idx} className="flex items-center text-sm text-gray-600">
                          <div className="w-1.5 h-1.5 bg-primary-600 rounded-full mr-2"></div>
                          {feature}
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    <button 
                      onClick={() => openDemoForm(course)}
                      className="w-full bg-accent-500 hover:bg-accent-600 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl flex items-center justify-center"
                    >
                      <Calendar className="mr-2 h-4 w-4" />
                      Book Free Demo
                    </button>
                    <button 
                      onClick={() => openPurchaseForm(course)}
                      className="w-full btn-primary group-hover:scale-105 transition-transform duration-200"
                    >
                      <Play className="mr-2 h-4 w-4" />
                      Purchase Course
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </button>
                    <button 
                      onClick={() => handleWhatsApp(course.title)}
                      className="w-full bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl flex items-center justify-center"
                    >
                      <MessageCircle className="mr-2 h-4 w-4" />
                      Ask on WhatsApp
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* No courses found message */}
          {filteredCourses.length === 0 && (
            <div className="text-center py-12" data-aos="fade-up">
              <div className="text-gray-400 mb-4">
                <Code className="h-16 w-16 mx-auto" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No courses found</h3>
              <p className="text-gray-600">Try selecting a different category or check back later for new courses.</p>
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <div data-aos="fade-up">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-6 font-heading">
              Ready to Start Learning?
            </h2>
            <p className="text-xl text-gray-600 mb-8 max-w-3xl mx-auto">
              Join thousands of students who have already transformed their careers with our expert-led courses.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
              <button 
                onClick={() => {
                  setSelectedCourse('courses');
                  setIsDemo(false);
                  setIsFormOpen(true);
                }}
                className="btn-primary text-lg px-8 py-4"
              >
                Contact Us
                <ArrowRight className="ml-2 h-5 w-5" />
              </button>
              <button 
                onClick={() => handleWhatsApp('courses')}
                className="bg-green-500 hover:bg-green-600 text-white font-semibold py-4 px-8 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl flex items-center"
              >
                <MessageCircle className="mr-2 h-5 w-5" />
                Chat with Us
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Contact Form Modal */}
      <ContactForm 
        isOpen={isFormOpen}
        onClose={closeForm}
        courseTitle={selectedCourse}
        isDemo={isDemo}
      />
    </div>
  );
};

export default Courses;
