  const startAssessment = (assessment) => {
    setSelectedAssessment(assessment);
    setCurrentQuestion(0);
    setAnswers({});
    setIsTestActive(true);
    setTestResults(null);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    setAnswers({
      ...answers,
      [questionId]: answerIndex
    });
  };

  const nextQuestion = () => {
    if (currentQuestion < selectedAssessment.questionsList.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const previousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const submitAssessment = () => {
    const totalQuestions = selectedAssessment.questionsList.length;
    let correctAnswers = 0;

    selectedAssessment.questionsList.forEach(question => {
      if (answers[question.id] === question.correctAnswer) {
        correctAnswers++;
      }
    });

    const score = Math.round((correctAnswers / totalQuestions) * 100);
    const passed = score >= selectedAssessment.passingScore;

    // Store results but don't show them yet
    const results = {
      score,
      correctAnswers,
      totalQuestions,
      passed,
      answers: { ...answers }
    };

    // Show submission modal first to collect student information
    setShowSubmissionModal(true);
    
    // Store results temporarily
    setTestResults(results);

    if (passed && !completedAssessments.includes(selectedAssessment.id)) {
      setCompletedAssessments([...completedAssessments, selectedAssessment.id]);
    }
  };

  const handleSubmitResults = async () => {
    if (!studentInfo.name || !studentInfo.email) {
      alert('Please fill in both name and email');
      return;
    }

    setIsSubmitting(true);

    try {
      // Prepare assessment results for email
      const assessmentResults = {
        studentName: studentInfo.name,
        studentEmail: studentInfo.email,
        assessmentTitle: selectedAssessment.title,
        assessmentCategory: selectedAssessment.category,
        score: testResults.score,
        correctAnswers: testResults.correctAnswers,
        totalQuestions: testResults.totalQuestions,
        passed: testResults.passed,
        completedAt: new Date().toLocaleString(),
        questionsAndAnswers: selectedAssessment.questionsList.map(question => ({
          question: question.question,
          studentAnswer: testResults.answers[question.id] !== undefined 
            ? question.options[testResults.answers[question.id]] 
            : 'Not answered',
          correctAnswer: question.options[question.correctAnswer],
          isCorrect: testResults.answers[question.id] === question.correctAnswer
        }))
      };

      // Prepare email template parameters
      const templateParams = {
        to_email: studentInfo.email,
        student_name: studentInfo.name,
        assessment_title: selectedAssessment.title,
        score: testResults.score,
        total_questions: testResults.totalQuestions,
        correct_answers: testResults.correctAnswers,
        passed: testResults.passed ? 'Yes' : 'No',
        completed_at: new Date().toLocaleString(),
        questions_summary: assessmentResults.questionsAndAnswers.map((qa, index) => 
          `${index + 1}. ${qa.question}\n   Student Answer: ${qa.studentAnswer}\n   Correct Answer: ${qa.correctAnswer}\n   Result: ${qa.isCorrect ? '✓ Correct' : '✗ Incorrect'}\n`
        ).join('\n')
      };

      // Send email using EmailJS
      await emailjs.send(
        EMAILJS_SERVICE_ID,
        EMAILJS_TEMPLATE_ID,
        templateParams,
        EMAILJS_USER_ID
      );

      alert('Assessment results have been sent to your email!');
      
      // Reset form and close modal, then show results
      setStudentInfo({ name: '', email: '' });
      setShowSubmissionModal(false);
      setIsSubmitting(false);

    } catch (error) {
      console.error('Error sending email:', error);
      alert('There was an error sending the results. Please try again.');
      setIsSubmitting(false);
    }
  };

