import React, { useState } from 'react';
import { 
  CheckCircle, 
  Circle, 
  Play, 
  Clock, 
  Calendar,
  BookOpen,
  Code,
  Target,
  Award,
  Users,
  X
} from 'lucide-react';

const Tasks = () => {
  const [enabledTasks, setEnabledTasks] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);
  const [showCompletionModal, setShowCompletionModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [studentInfo, setStudentInfo] = useState({
    name: '',
    courseName: '',
    githubLink: ''
  });

  const tasks = [
    {
      id: 1,
      title: "Task 1: HTML Basics",
      description: "Learn HTML fundamentals and create your first webpage",
      duration: "2 hours",
      difficulty: "Beginner",
      category: "Web Development",
      requirements: [
        "Complete HTML tutorial",
        "Create a simple webpage",
        "Submit assignment"
      ],
      instructions: `
# Task 1: HTML Basics

## Objective
Create a simple webpage using HTML to demonstrate your understanding of basic HTML elements.

## Requirements
1. Create an HTML file with proper structure
2. Include at least 3 different heading levels (h1, h2, h3)
3. Add paragraphs with some text content
4. Include an ordered list and an unordered list
5. Add at least 2 images (you can use placeholder images)
6. Create a simple form with name and email fields
7. Add a table with at least 3 rows and 3 columns

## Submission
- Save your file as "task1.html"
- Test your webpage in a browser
- Submit the file through the assignment portal

## Evaluation Criteria
- Proper HTML structure
- Correct use of HTML tags
- Clean and readable code
- All requirements met
      `,
      dueDate: "2024-02-15",
      points: 100,
      taskLink: "https://fatimatabassum743.github.io/WebDevelopmentTasks/"
    },
    {
      id: 2,
      title: "Task 2: CSS Styling",
      description: "Style your HTML webpage with CSS",
      duration: "3 hours",
      difficulty: "Beginner",
      category: "Web Development",
      requirements: [
        "Learn CSS basics",
        "Style your HTML page",
        "Make it responsive"
      ],
      instructions: `
# Task 2: CSS Styling

## Objective
Style your HTML webpage from Task 1 using CSS to make it visually appealing.

## Requirements
1. Create a separate CSS file
2. Apply consistent color scheme
3. Style headings with different fonts and colors
4. Add hover effects to links
5. Style your form elements
6. Make the page responsive (mobile-friendly)
7. Add some animations or transitions

## CSS Concepts to Use
- Selectors (element, class, ID)
- Box model (margin, padding, border)
- Flexbox or Grid for layout
- Media queries for responsiveness
- Pseudo-classes (:hover, :focus)

## Submission
- Create "styles.css" file
- Link CSS to your HTML file
- Test on different screen sizes
- Submit both HTML and CSS files

## Evaluation Criteria
- Clean and organized CSS
- Responsive design
- Consistent styling
- Good user experience
      `,
      dueDate: "2024-02-20",
      points: 150,
      taskLink: "https://fatimatabassum743.github.io/WebDevelopmentTasks/"
    },
    {
      id: 3,
      title: "Task 3: JavaScript Fundamentals",
      description: "Add interactivity to your webpage with JavaScript",
      duration: "4 hours",
      difficulty: "Intermediate",
      category: "Web Development",
      requirements: [
        "Learn JavaScript basics",
        "Add interactive features",
        "Handle form submission"
      ],
      instructions: `
# Task 3: JavaScript Fundamentals

## Objective
Add JavaScript functionality to your webpage to make it interactive.

## Requirements
1. Create a separate JavaScript file
2. Add form validation
3. Create a simple calculator
4. Add a dark/light mode toggle
5. Implement a simple quiz
6. Add smooth scrolling navigation
7. Create a countdown timer

## JavaScript Concepts to Use
- Variables and data types
- Functions and event handlers
- DOM manipulation
- Form handling
- Local storage
- Conditional statements
- Loops

## Submission
- Create "script.js" file
- Link JavaScript to your HTML file
- Test all functionality
- Submit all files (HTML, CSS, JS)

## Evaluation Criteria
- Working JavaScript functionality
- Clean and readable code
- Error handling
- User-friendly interactions
      `,
      dueDate: "2024-02-25",
      points: 200,
      taskLink: "https://fatimatabassum743.github.io/WebDevelopmentTasks/"
    },
    {
      id: 4,
      title: "Task 4: React Components",
      description: "Build a React application with components",
      duration: "6 hours",
      difficulty: "Intermediate",
      category: "Web Development",
      requirements: [
        "Set up React project",
        "Create functional components",
        "Use props and state"
      ],
      instructions: `
# Task 4: React Components

## Objective
Create a React application demonstrating component-based architecture.

## Requirements
1. Set up a new React project using Create React App
2. Create at least 5 functional components
3. Use props to pass data between components
4. Implement state management with useState
5. Create a simple todo list application
6. Add form handling with controlled components
7. Implement conditional rendering

## React Concepts to Use
- Functional components
- Props and prop drilling
- useState hook
- Event handling
- Conditional rendering
- List rendering with keys
- Component composition

## Submission
- Complete React application
- All components working properly
- Clean and organized code structure
- Deploy to GitHub Pages or similar

## Evaluation Criteria
- Proper React implementation
- Component reusability
- Clean code structure
- Working functionality
      `,
      dueDate: "2024-03-01",
      points: 250,
      taskLink: "https://fatimatabassum743.github.io/WebDevelopmentTasks/"
    },
    {
      id: 5,
      title: "Task 5: API Integration",
      description: "Connect your React app to external APIs",
      duration: "5 hours",
      difficulty: "Advanced",
      category: "Web Development",
      requirements: [
        "Learn API concepts",
        "Fetch data from APIs",
        "Handle loading states"
      ],
      instructions: `
# Task 5: API Integration

## Objective
Integrate external APIs into your React application.

## Requirements
1. Choose a public API (e.g., JSONPlaceholder, OpenWeatherMap)
2. Use fetch or axios to make API calls
3. Implement loading states
4. Handle API errors gracefully
5. Display fetched data in your components
6. Add search or filter functionality
7. Implement pagination if applicable

## API Integration Concepts
- HTTP methods (GET, POST, PUT, DELETE)
- Async/await or promises
- Error handling
- Loading states
- Data transformation
- CORS and API keys

## Submission
- Working API integration
- Error handling implementation
- Loading states
- Clean user interface
- Documentation of API used

## Evaluation Criteria
- Successful API integration
- Proper error handling
- Good user experience
- Clean code implementation
      `,
      dueDate: "2024-03-05",
      points: 300,
      taskLink: "https://fatimatabassum743.github.io/WebDevelopmentTasks/"
    }
  ];

  const toggleTask = (taskId) => {
    if (enabledTasks.includes(taskId)) {
      setEnabledTasks(enabledTasks.filter(id => id !== taskId));
    } else {
      setEnabledTasks([...enabledTasks, taskId]);
    }
  };

  const handleMarkComplete = (task) => {
    setSelectedTask(task);
    setShowCompletionModal(true);
  };

  const handleSubmitCompletion = () => {
    if (!studentInfo.name || !studentInfo.courseName || !studentInfo.githubLink) {
      alert('Please fill in all fields');
      return;
    }

    // Create completion record
    const completionRecord = {
      id: Date.now(),
      taskId: selectedTask.id,
      taskTitle: selectedTask.title,
      studentName: studentInfo.name,
      courseName: studentInfo.courseName,
      githubLink: studentInfo.githubLink,
      completedAt: new Date().toISOString(),
      points: selectedTask.points
    };

    // Get existing completions from localStorage
    const existingCompletions = JSON.parse(localStorage.getItem('taskCompletions') || '[]');
    
    // Add new completion
    const updatedCompletions = [...existingCompletions, completionRecord];
    
    // Save to localStorage
    localStorage.setItem('taskCompletions', JSON.stringify(updatedCompletions));

    // Mark task as completed in state
    if (!completedTasks.includes(selectedTask.id)) {
      setCompletedTasks([...completedTasks, selectedTask.id]);
    }

    // Reset form and close modal
    setStudentInfo({ name: '', courseName: '', githubLink: '' });
    setShowCompletionModal(false);
    setSelectedTask(null);

    alert('Task completed successfully! Your submission has been recorded.');
  };

  const handleStartTask = (taskLink) => {
    window.open(taskLink, '_blank');
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'Beginner': return 'bg-green-100 text-green-800';
      case 'Intermediate': return 'bg-yellow-100 text-yellow-800';
      case 'Advanced': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'Web Development': return <Code className="w-5 h-5" />;
      case 'Programming': return <Code className="w-5 h-5" />;
      case 'AI/ML': return <Target className="w-5 h-5" />;
      case 'Digital Marketing': return <Users className="w-5 h-5" />;
      default: return <BookOpen className="w-5 h-5" />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            <Target className="inline-block w-8 h-8 mr-3 text-blue-600" />
            Course Tasks
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Complete tasks to progress through your course. Click on tasks to enable them, 
            then work through the requirements to earn points.
          </p>
        </div>

        {/* Progress Summary */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{tasks.length}</div>
              <div className="text-sm text-gray-600">Total Tasks</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{enabledTasks.length}</div>
              <div className="text-sm text-gray-600">Enabled</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">{completedTasks.length}</div>
              <div className="text-sm text-gray-600">Completed</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">
                {completedTasks.reduce((total, taskId) => {
                  const task = tasks.find(t => t.id === taskId);
                  return total + (task ? task.points : 0);
                }, 0)}
              </div>
              <div className="text-sm text-gray-600">Points Earned</div>
            </div>
          </div>
        </div>

        {/* Tasks Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {tasks.map((task) => {
            const isEnabled = enabledTasks.includes(task.id);
            const isCompleted = completedTasks.includes(task.id);
            
            return (
              <div 
                key={task.id} 
                className={`bg-white rounded-lg shadow-md overflow-hidden transition-all duration-300 ${
                  isEnabled ? 'ring-2 ring-blue-500' : ''
                } ${isCompleted ? 'ring-2 ring-green-500' : ''}`}
              >
                <div className="p-6">
                  {/* Task Header */}
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center space-x-3">
                      <button
                        onClick={() => toggleTask(task.id)}
                        className={`p-2 rounded-full transition-colors duration-200 ${
                          isEnabled 
                            ? 'bg-blue-100 text-blue-600 hover:bg-blue-200' 
                            : 'bg-gray-100 text-gray-400 hover:bg-gray-200'
                        }`}
                      >
                        {isEnabled ? <CheckCircle className="w-5 h-5" /> : <Circle className="w-5 h-5" />}
                      </button>
                      <div>
                        <h3 className="text-xl font-semibold text-gray-900">
                          {task.title}
                        </h3>
                        <div className="flex items-center space-x-2 mt-1">
                          <span className={`inline-block px-2 py-1 text-xs font-medium rounded ${getDifficultyColor(task.difficulty)}`}>
                            {task.difficulty}
                          </span>
                          <span className="inline-block bg-blue-100 text-blue-800 text-xs font-medium px-2 py-1 rounded">
                            {task.category}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-blue-600">{task.points}</div>
                      <div className="text-xs text-gray-500">points</div>
                    </div>
                  </div>

                  {/* Task Description */}
                  <p className="text-gray-600 mb-4">
                    {task.description}
                  </p>

                  {/* Task Details */}
                  <div className="flex items-center space-x-4 text-sm text-gray-500 mb-4">
                    <div className="flex items-center">
                      <Clock className="w-4 h-4 mr-1" />
                      {task.duration}
                    </div>
                    <div className="flex items-center">
                      <Calendar className="w-4 h-4 mr-1" />
                      Due: {new Date(task.dueDate).toLocaleDateString()}
                    </div>
                  </div>

                  {/* Requirements */}
                  {isEnabled && (
                    <div className="mb-4">
                      <h4 className="font-medium text-gray-900 mb-2">Requirements:</h4>
                      <ul className="space-y-1">
                        {task.requirements.map((req, index) => (
                          <li key={index} className="flex items-center text-sm text-gray-600">
                            <Circle className="w-3 h-3 mr-2 text-gray-400" />
                            {req}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {/* Action Buttons */}
                  <div className="flex space-x-3">
                    {isEnabled && !isCompleted && (
                      <button
                        onClick={() => handleMarkComplete(task)}
                        className="flex-1 inline-flex items-center justify-center px-4 py-2 bg-green-600 text-white text-sm font-medium rounded-lg hover:bg-green-700 focus:ring-4 focus:ring-green-300 transition-colors duration-200"
                      >
                        <Award className="w-4 h-4 mr-2" />
                        Mark Complete
                      </button>
                    )}
                    
                    {isEnabled && (
                      <button 
                        onClick={() => handleStartTask(task.taskLink)}
                        className="flex-1 inline-flex items-center justify-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-lg hover:bg-blue-700 focus:ring-4 focus:ring-blue-300 transition-colors duration-200"
                      >
                        <Play className="w-4 h-4 mr-2" />
                        {isCompleted ? 'Review Task' : 'Start Task'}
                      </button>
                    )}
                  </div>

                  {/* Status */}
                  <div className="mt-4 pt-4 border-t border-gray-200">
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-gray-500">
                        Status: {isCompleted ? 'Completed' : isEnabled ? 'Enabled' : 'Disabled'}
                      </span>
                      {isCompleted && (
                        <span className="text-green-600 font-medium">
                           Completed
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Instructions */}
        <div className="mt-8 bg-blue-50 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-blue-900 mb-3">How to Use Tasks</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-blue-800">
            <div className="flex items-start">
              <Circle className="w-5 h-5 mr-2 mt-0.5 text-blue-600" />
              <div>
                <strong>1. Enable Tasks:</strong> Click the circle icon next to any task to enable it
              </div>
            </div>
            <div className="flex items-start">
              <Play className="w-5 h-5 mr-2 mt-0.5 text-blue-600" />
              <div>
                <strong>2. Start Working:</strong> Click "Start Task" to begin working on the requirements
              </div>
            </div>
            <div className="flex items-start">
              <Award className="w-5 h-5 mr-2 mt-0.5 text-blue-600" />
              <div>
                <strong>3. Mark Complete:</strong> Click "Mark Complete" when you finish the task
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Completion Modal */}
      {showCompletionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                Complete Task: {selectedTask?.title}
              </h3>
              <button
                onClick={() => {
                  setShowCompletionModal(false);
                  setSelectedTask(null);
                  setStudentInfo({ name: '', courseName: '', githubLink: '' });
                }}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="w-5 h-5" />
              </button>
            </div>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Your Name *
                </label>
                <input
                  type="text"
                  value={studentInfo.name}
                  onChange={(e) => setStudentInfo({ ...studentInfo, name: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter your full name"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Course Name *
                </label>
                <select
                  value={studentInfo.courseName}
                  onChange={(e) => setStudentInfo({ ...studentInfo, courseName: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="">Select your course</option>
                  <option value="Web Development">Web Development</option>
                  <option value="MERN Stack">MERN Stack</option>
                  <option value="AI & Machine Learning">AI & Machine Learning</option>
                  <option value="Digital Marketing">Digital Marketing</option>
                  <option value="Python Programming">Python Programming</option>
                  <option value="Linux Administration">Linux Administration</option>
                  <option value="AWS Cloud Computing">AWS Cloud Computing</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  GitHub Link *
                </label>
                <input
                  type="url"
                  value={studentInfo.githubLink}
                  onChange={(e) => setStudentInfo({ ...studentInfo, githubLink: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="https://github.com/yourusername/project"
                />
              </div>

              <div className="bg-blue-50 p-3 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Note:</strong> Please ensure your GitHub repository contains the completed task work and is publicly accessible for review.
                </p>
              </div>
            </div>

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => {
                  setShowCompletionModal(false);
                  setSelectedTask(null);
                  setStudentInfo({ name: '', courseName: '', githubLink: '' });
                }}
                className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors duration-200"
              >
                Cancel
              </button>
              <button
                onClick={handleSubmitCompletion}
                className="flex-1 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors duration-200"
              >
                Submit Completion
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tasks;
