import React, { useRef, useState } from 'react';
import emailjs from 'emailjs-com';
import { Send, X } from 'lucide-react';

const SERVICE_ID = 'service_lzv0n76';
const TEMPLATE_ID = 'template_hywc48o';
const USER_ID = '92m3Tvs-ztcNiRG6X';

const ContactForm = ({ isOpen, onClose, courseTitle = null, isDemo = false }) => {
  const form = useRef();
  const [sent, setSent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const sendEmail = (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    // Add course title to form data if it's a demo booking
    const formData = new FormData(form.current);
    if (courseTitle) {
      formData.append('course_title', courseTitle);
    }
    if (isDemo) {
      formData.append('demo_request', 'true');
    }

    emailjs.sendForm(SERVICE_ID, TEMPLATE_ID, form.current, USER_ID)
      .then((result) => {
        setSent(true);
        setLoading(false);
        setTimeout(() => {
          onClose();
          setSent(false);
        }, 3000);
      }, (error) => {
        setError('Failed to send message. Please try again.');
        setLoading(false);
      });
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          {/* Header */}
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-900">
              {isDemo ? 'Book Free Demo' : 'Contact Us'}
            </h2>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 transition-colors"
            >
              <X className="h-6 w-6" />
            </button>
          </div>

          {/* Course Title Display */}
          {courseTitle && (
            <div className="mb-4 p-3 bg-primary-50 rounded-lg">
              <p className="text-sm text-gray-600">Course:</p>
              <p className="font-semibold text-primary-600">{courseTitle}</p>
            </div>
          )}

          {/* Form */}
          <form ref={form} onSubmit={sendEmail} className="space-y-4">
            <div>
              <label className="block mb-2 font-medium text-gray-700">Name *</label>
              <input 
                type="text" 
                name="user_name" 
                required 
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-600" 
                placeholder="Your full name"
              />
            </div>
            
            <div>
              <label className="block mb-2 font-medium text-gray-700">Email *</label>
              <input 
                type="email" 
                name="user_email" 
                required 
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-600" 
                placeholder="your.email@example.com"
              />
            </div>
            
            <div>
              <label className="block mb-2 font-medium text-gray-700">Phone Number</label>
              <input 
                type="tel" 
                name="phone" 
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-600" 
                placeholder="+1 (555) 123-4567"
              />
            </div>
            
            <div>
              <label className="block mb-2 font-medium text-gray-700">Subject *</label>
              <input 
                type="text" 
                name="subject" 
                required 
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-600" 
                placeholder={isDemo ? "Demo request for course" : "What can we help you with?"}
                defaultValue={isDemo ? `Demo request for ${courseTitle}` : ''}
              />
            </div>
            
            <div>
              <label className="block mb-2 font-medium text-gray-700">Message *</label>
              <textarea 
                name="message" 
                rows="4" 
                required 
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-600"
                placeholder={isDemo ? "Tell us about your learning goals and preferred demo time..." : "Your message..."}
                defaultValue={isDemo ? `I'm interested in booking a free demo for the ${courseTitle} course. Please provide more details about the curriculum and schedule.` : ''}
              ></textarea>
            </div>

            {/* Hidden fields for course info */}
            {courseTitle && (
              <input type="hidden" name="course_title" value={courseTitle} />
            )}
            {isDemo && (
              <input type="hidden" name="demo_request" value="true" />
            )}

            <button 
              type="submit" 
              className="w-full btn-primary flex items-center justify-center" 
              disabled={loading}
            >
              <Send className="mr-2 h-5 w-5" />
              {loading ? 'Sending...' : (isDemo ? 'Book Demo' : 'Send Message')}
            </button>
            
            {sent && (
              <p className="text-green-600 font-medium text-center">
                {isDemo ? 'Demo request sent successfully! We\'ll contact you soon.' : 'Message sent successfully!'}
              </p>
            )}
            
            {error && (
              <p className="text-red-600 font-medium text-center">{error}</p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContactForm;
